    private void btnCadastrarAlunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarAlunoActionPerformed
       
        if (JOptionPane.showConfirmDialog(null, "Deseja Cadastrar este registro?", "Atenção", JOptionPane.YES_NO_OPTION, JOptionPane.WARNING_MESSAGE) == 0){
            String nome;

            int idade;
            int cod_cidade = 0;

            nome = txtNomeAluno.getText();

            idade = Integer.parseInt(txtIdade.getText());

            cod_cidade = id_cidade.get(cmbCidade.getSelectedIndex()); // Busca o id_cidade da cidade selecionada no combobox


            AlunosDTO objAlunoDTO = new AlunosDTO();

            objAlunoDTO.setNome(nome);
            objAlunoDTO.setIdade(idade);

            objAlunoDTO.setCod_Cidade(cod_cidade);

            AlunosDAO objAlunoDAO = new AlunosDAO();
            objAlunoDAO.cadastrarAluno(objAlunoDTO);

            limparcampos();
        }
    }//GEN-LAST:event_btnCadastrarAlunoActionPerformed

