    private void btnListarAlunosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnListarAlunosActionPerformed
        try {
            AlunosDAO objAlunoDAO = new AlunosDAO();
            DefaultTableModel model = (DefaultTableModel) tblAlunos.getModel();
            model.setNumRows(0);
            ArrayList<AlunosDTO> lista = objAlunoDAO.ListarAlunos();
            for (int num = 0; num < lista.size(); num++) {
                model.addRow(new Object[]{
                    lista.get(num).getId(),
                    lista.get(num).getNome(),                    
                    lista.get(num).getCidade(),                    
                    lista.get(num).getIdade(),});
            }
        } catch (Exception e) {
        }
    }//GEN-LAST:event_btnListarAlunosActionPerformed

