    private void btnExcluirAlunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirAlunoActionPerformed
        
        DefaultTableModel modelo = (DefaultTableModel) tblAlunos.getModel();
        int linhaSelecionada = tblAlunos.getSelectedRow();
        
        if (linhaSelecionada != -1) { // Verifica se alguma linha está selecionada
            // Supondo que o ID esteja na primeira coluna da tabela (índice 0)
            Object idSelecionado = tblAlunos.getValueAt(linhaSelecionada, 0);
            
            if (JOptionPane.showConfirmDialog(null, "Deseja excluir este registro?", "Atenção", JOptionPane.YES_NO_OPTION, JOptionPane.WARNING_MESSAGE) == 0) {
            int id_aluno = 0;

            id_aluno = ((Integer) idSelecionado);

            AlunosDTO objAluno = new AlunosDTO();
            objAluno.setId(id_aluno);

            AlunosDAO objAlunoDAO = new AlunosDAO();
            objAlunoDAO.excluirAluno(objAluno);
            
            modelo.removeRow(linhaSelecionada);
            }
        } 
        else {
            System.out.println("Nenhuma linha selecionada.");
        }
    }//GEN-LAST:event_btnExcluirAlunoActionPerformed

